import { SET_USER, CLEAR_USER, SET_CONTACTS } from './actions';

const initialState = {
  user: null,
  contacts: [],
};

export const userReducer = (state = initialState.user, action) => {
  switch (action.type) {
    case SET_USER:
      return action.payload;
    case CLEAR_USER:
      return null;
    default:
      return state;
  }
};

export const contactsReducer = (state = initialState.contacts, action) => {
  switch (action.type) {
    case SET_CONTACTS:
      return action.payload;
    default:
      return state;
  }
};
