import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const token = useSelector(state => state.auth.token);

  const handleLogout = () => {
    dispatch(clearAuthToken());
  };

  const hideHomeLink = location.pathname === '/goit-react-hw-08-phonebook/home' || location.pathname === '/goit-react-hw-08-phonebook';

  const navItemsNotLoggedIn = (
    <ul>
      {!hideHomeLink && <li><NavLink to="/home">Home</NavLink></li>}
      {location.pathname !== '/goit-react-hw-08-phonebook/login' && <li><NavLink to="/login">Login</NavLink></li>}
      {location.pathname !== '/goit-react-hw-08-phonebook/register' && <li><NavLink to="/register">Register</NavLink></li>}
    </ul>
  );

  const navItemsLoggedIn = (
    <ul>
      {!hideHomeLink && <li><NavLink to="/home">Home</NavLink></li>}
      {location.pathname !== '/goit-react-hw-08-phonebook/contacts' && <li><NavLink to="/contacts">Contacts</NavLink></li>}
      <li><button onClick={handleLogout}>Logout</button></li>
    </ul>
  );

  return (
    <nav>
      {!token ? navItemsNotLoggedIn : navItemsLoggedIn}
    </nav>
  );
}

export default Navigation;
