import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const token = useSelector(state => state.auth.token);
  const isContactsRoute = location.pathname === '/contacts';
  const isHomeRoute = location.pathname === '/';

  const handleLogout = () => {
    dispatch(clearAuthToken());
    // Additional logout actions can be added here
  };

  if (!token) {
    return (
      <nav>
        <NavLink to="/">Home</NavLink>
        <NavLink to="/login">Login</NavLink>
        <NavLink to="/register">Register</NavLink>
      </nav>
    );
  }

  return (
    <nav>
      {isContactsRoute && <NavLink to="/">Home</NavLink>}
      {isHomeRoute && <NavLink to="/contacts">Contacts</NavLink>}
      <button onClick={handleLogout}>Logout</button>
    </nav>
  );
}

export default Navigation;
