import React from 'react';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';
import { Flex, Button, Link, List, ListItem, Text } from '@chakra-ui/react';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const user = useSelector(state => state.user); // Accessing the user state
  const token = useSelector(state => state.auth.token);

  const handleLogout = () => {
    dispatch(clearAuthToken());
    navigate('/home');
  };

  const currentPath = location.pathname.replace(
    '/goit-react-hw-08-phonebook',
    ''
  );
  const isContactsPage = currentPath === '/contacts';

  return (
    <Flex as="nav" bg="blue.500" p={4} color="white" justifyContent="center">
      {token ? (
        <List styleType="none" display="flex" alignItems="center">
          {isContactsPage && user && (
            <ListItem mr={4}>
              <Text>{user.email}</Text> {/* Display user's email */}
            </ListItem>
          )}
          <ListItem mr={4}>
            <Button onClick={handleLogout}>Logout</Button>
          </ListItem>
        </List>
      ) : (
        // Navigation items for when not logged in
        <List styleType="none" display="flex" alignItems="center">
          {!hideHomeLink && (
            <ListItem mr={4}>
              <Link as={NavLink} to="/home">
                Home
              </Link>
            </ListItem>
          )}
          {!hideLoginLink && (
            <ListItem mr={4}>
              <Link as={NavLink} to="/login">
                Login
              </Link>
            </ListItem>
          )}
          {!hideRegisterLink && (
            <ListItem mr={4}>
              <Link as={NavLink} to="/register">
                Register
              </Link>
            </ListItem>
          )}
        </List>
      )}
    </Flex>
  );
}

export default Navigation;
