import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const token = useSelector(state => state.auth.token);
  const isLoginRoute = location.pathname === '/login';
  const isRegisterRoute = location.pathname === '/register';

  // Hide navigation on the home route
  if (location.pathname === '/') {
    return null;
  }

  const handleLogout = () => {
    // Add your logout logic here
    dispatch(clearAuthToken());
    // Additional logout actions can be added here
  };

  return (
    <nav>
      <NavLink to="/">Home</NavLink>

      {!token && (
        <>
          {!isLoginRoute && <NavLink to="/login">Login</NavLink>}
          {!isRegisterRoute && <NavLink to="/register">Register</NavLink>}
        </>
      )}

      {token && (
        <>
          <NavLink to="/contacts">Contacts</NavLink>
          <button onClick={handleLogout}>Logout</button> {/* Logout button */}
        </>
      )}
    </nav>
  );
}

export default Navigation;
