import React from 'react';
import { useSelector } from 'react-redux';
import { Text } from '@chakra-ui/react';

function UserMenu() {
  const user = useSelector(state => state.user); // Assuming the user's email is stored here

  return user ? (
    <Text color="white" mr={4}>
      {user.email}{' '}
      {/* Adjust this if the email is stored under a different key */}
    </Text>
  ) : null;
}

export default UserMenu;
