import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import myAPI from '../myAPI/myAPI'; // Update the path as necessary

function ContactForm({ onAddContact }) {
  const [contact, setContact] = useState({ name: '', number: '' });
  const token = useSelector(state => state.auth.token);

  const handleChange = event => {
    setContact({ ...contact, [event.target.name]: event.target.value });
  };

  const handleSubmit = async event => {
    event.preventDefault();
    if (!contact.name || !contact.number) {
      alert('Please fill in all fields');
      return;
    }

    // Additional validation can be added here based on your API requirements

    if (token) {
      try {
        const response = await myAPI.addContact(contact, token);
        if (response && response.id) {
          // Assuming the API returns the added contact with an 'id'
          onAddContact();
        } else {
          // Handle API response errors (e.g., show error message)
          console.error('Error adding contact:', response);
        }
      } catch (error) {
        console.error('Error adding contact:', error);
        // Handle network or other errors here
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="name"
        value={contact.name}
        onChange={handleChange}
        placeholder="Name"
      />
      <input
        name="number"
        value={contact.number}
        onChange={handleChange}
        placeholder="Number"
      />
      <button type="submit">Add Contact</button>
    </form>
  );
}

export default ContactForm;
