// Action Types
export const SET_USER = 'SET_USER';
export const CLEAR_USER = 'CLEAR_USER';
export const SET_CONTACTS = 'SET_CONTACTS';

// Action Creators
export const setUser = userData => ({
  type: SET_USER,
  payload: userData,
});

export const clearUser = () => ({
  type: CLEAR_USER,
});

export const setContacts = contacts => ({
  type: SET_CONTACTS,
  payload: contacts,
});
