import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const token = useSelector(state => state.auth.token);
  const isContactsRoute = location.pathname === '/contacts';
  const isHomeRoute = location.pathname === '/';

  const handleLogout = () => {
    dispatch(clearAuthToken());
    // Additional logout actions can be added here
  };

  return (
    <nav>
      {token ? (
        <>
          {isContactsRoute && <NavLink to="/">Home</NavLink>}
          {isHomeRoute && <NavLink to="/contacts">Contacts</NavLink>}
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <>
          <NavLink to="/">Home</NavLink>
          {!isHomeRoute && <NavLink to="/login">Login</NavLink>}
          {!isHomeRoute && <NavLink to="/register">Register</NavLink>}
        </>
      )}
    </nav>
  );
}

export default Navigation;
