import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { actions } from '../../redux/store';
import PropTypes from 'prop-types';
import ContactItem from '../ContactItem/ContactItem';

const { deleteContactAsync, updateFilter } = actions;

const ContactList = () => {
  const [editableContactId, setEditableContactId] = useState(null);
  const contacts = useSelector(state => state.contacts);
  const filter = useSelector(state => state.filter);
  const dispatch = useDispatch();

  const handleChange = event => {
    dispatch(updateFilter(event.target.value));
  };

  const filteredContacts = contacts.filter(contact =>
    contact.name.toLowerCase().includes(filter.toLowerCase())
  );

  const handleEdit = id => {
    setEditableContactId(id);
  };

  return (
    <>
      <label>
        Find contacts by name
        <input
          className="input"
          type="text"
          value={filter}
          onChange={handleChange}
        />
      </label>
      <ul>
        {filteredContacts.map(contact => (
          <ContactItem
            key={contact.id}
            contact={contact}
            onDelete={() => dispatch(deleteContactAsync(contact.id))}
            onEdit={() => handleEdit(contact.id)}
            isEditing={editableContactId === contact.id}
          />
        ))}
      </ul>
    </>
  );
};

ContactList.propTypes = {
  contacts: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      number: PropTypes.string.isRequired,
    })
  ),
  filter: PropTypes.string,
};

export default ContactList;
