import React, { useState, useEffect } from 'react';
import ContactForm from '../ContactForm/ContactForm';
import ContactItem from '../ContactItem/ContactItem';
import ContactList from '../ContactList/ContactList';
import myAPI from '../myAPI/myAPI';
import { useSelector } from 'react-redux';

function Contacts() {
  const [contacts, setContacts] = useState([]);
  const token = useSelector(state => state.auth.token);

  useEffect(() => {
    const fetchContacts = async () => {
      if (token) {
        const fetchedContacts = await myAPI.fetchContacts(token);
        if (Array.isArray(fetchedContacts)) {
          setContacts(fetchedContacts);
        }
      }
    };
    fetchContacts();
  }, [token]);

  const handleAddContact = async newContact => {
    if (token) {
      const addedContact = await myAPI.addContact(newContact, token);
      setContacts([...contacts, addedContact]);
    }
  };

  const handleEditContact = async (contactId, updatedContact) => {
    if (token) {
      await myAPI.editContact(contactId, updatedContact, token);
      // Update local contacts state accordingly
    }
  };

  const handleDeleteContact = async contactId => {
    if (token) {
      await myAPI.deleteContact(contactId, token);
      setContacts(contacts.filter(contact => contact.id !== contactId));
    }
  };

  return (
    <div>
      <ContactForm onAddContact={handleAddContact} />
      <ContactList
        contacts={contacts}
        onEditContact={handleEditContact}
        onDeleteContact={handleDeleteContact}
      />
    </div>
  );
}

export default Contacts;
