import {
  Alert,
  AlertDescription,
  AlertIcon,
  AlertProps,
  AlertTitle,
} from "@chakra-ui/alert"
import { chakra } from "@chakra-ui/system"
import { CloseButton } from "@chakra-ui/close-button"
import { runIfFn } from "@chakra-ui/shared-utils"
import type { UseToastOptions } from "./use-toast"
import type { RenderProps, ToastId } from "./toast.types"
import { getToastPlacement } from "./toast.placement"
import { toastStore } from "./toast.store"

export interface ToastProps
  extends UseToastOptions,
    Omit<AlertProps, keyof UseToastOptions> {
  onClose?: () => void
}

/**
 * The `Toast` component is used to give feedback to users after an action has taken place.
 *
 * @see Docs https://chakra-ui.com/docs/components/toast
 */
export const Toast: React.FC<ToastProps> = (props) => {
  const {
    status,
    variant = "solid",
    id,
    title,
    isClosable,
    onClose,
    description,
    colorScheme,
    icon,
  } = props

  const ids = id
    ? {
        root: `toast-${id}`,
        title: `toast-${id}-title`,
        description: `toast-${id}-description`,
      }
    : undefined

  return (
    <Alert
      addRole={false}
      status={status}
      variant={variant}
      id={ids?.root}
      alignItems="start"
      borderRadius="md"
      boxShadow="lg"
      paddingEnd={8}
      textAlign="start"
      width="auto"
      colorScheme={colorScheme}
    >
      <AlertIcon>{icon}</AlertIcon>
      <chakra.div flex="1" maxWidth="100%">
        {title && <AlertTitle id={ids?.title}>{title}</AlertTitle>}
        {description && (
          <AlertDescription id={ids?.description} display="block">
            {description}
          </AlertDescription>
        )}
      </chakra.div>
      {isClosable && (
        <CloseButton
          size="sm"
          onClick={onClose}
          position="absolute"
          insetEnd={1}
          top={1}
        />
      )}
    </Alert>
  )
}

export function createRenderToast(
  options: UseToastOptions & {
    toastComponent?: React.FC<ToastProps>
  } = {},
) {
  const { render, toastComponent: ToastComponent = Toast } = options
  const renderToast: React.FC<RenderProps> = (props) => {
    if (typeof render === "function") {
      return render({ ...props, ...options }) as JSX.Element
    }
    return <ToastComponent {...props} {...options} />
  }
  return renderToast
}

type UseToastPromiseOption = Omit<UseToastOptions, "status">

export function createToastFn(
  dir: "ltr" | "rtl",
  defaultOptions?: UseToastOptions,
) {
  const normalizeToastOptions = (options?: UseToastOptions) => ({
    ...defaultOptions,
    ...options,
    position: getToastPlacement(
      options?.position ?? defaultOptions?.position,
      dir,
    ),
  })

  const toast = (options?: UseToastOptions) => {
    const normalizedToastOptions = normalizeToastOptions(options)
    const Message = createRenderToast(normalizedToastOptions)
    return toastStore.notify(Message, normalizedToastOptions)
  }

  toast.update = (id: ToastId, options: Omit<UseToastOptions, "id">) => {
    toastStore.update(id, normalizeToastOptions(options))
  }

  toast.promise = <Result extends any, Err extends Error = Error>(
    promise: Promise<Result>,
    options: {
      success: MaybeFunction<UseToastPromiseOption, [Result]>
      error: MaybeFunction<UseToastPromiseOption, [Err]>
      loading: UseToastPromiseOption
    },
  ) => {
    const id = toast({
      ...options.loading,
      status: "loading",
      duration: null,
    })

    promise
      .then((data) =>
        toast.update(id, {
          status: "success",
          duration: 5_000,
          ...runIfFn(options.success, data),
        }),
      )
      .catch((error) =>
        toast.update(id, {
          status: "error",
          duration: 5_000,
          ...runIfFn(options.error, error),
        }),
      )
  }

  toast.closeAll = toastStore.closeAll
  toast.close = toastStore.close
  toast.isActive = toastStore.isActive

  return toast
}

export type CreateToastFnReturn = ReturnType<typeof createToastFn>

type MaybeFunction<T, Args extends unknown[] = []> = T | ((...args: Args) => T)
import { createRenderToast } from "./toast"
import { ToastPosition } from "./toast.placement"
import { CreateToastOptions, ToastMethods } from "./toast.provider"
import type { ToastId, ToastMessage, ToastState } from "./toast.types"
import { findToast, getToastPosition } from "./toast.utils"

type ToastStore = ToastMethods & {
  getState: () => ToastState
  subscribe: (onStoreChange: () => void) => () => void
  removeToast: (id: ToastId, position: ToastPosition) => void
}

const initialState = {
  top: [],
  "top-left": [],
  "top-right": [],
  "bottom-left": [],
  bottom: [],
  "bottom-right": [],
}

/**
 * Store to track all the toast across all positions
 */
export const toastStore = createStore(initialState)

function createStore(initialState: ToastState): ToastStore {
  let state = initialState
  const listeners = new Set<() => void>()

  const setState = (setStateFn: (values: ToastState) => ToastState) => {
    state = setStateFn(state)
    listeners.forEach((l) => l())
  }

  return {
    getState: () => state,

    subscribe: (listener) => {
      listeners.add(listener)
      return () => {
        // Delete all toasts on unmount
        setState(() => initialState)
        listeners.delete(listener)
      }
    },

    /**
     * Delete a toast record at its position
     */
    removeToast: (id, position) => {
      setState((prevState) => ({
        ...prevState,
        // id may be string or number
        // eslint-disable-next-line eqeqeq
        [position]: prevState[position].filter((toast) => toast.id != id),
      }))
    },

    notify: (message, options) => {
      const toast = createToast(message, options)
      const { position, id } = toast

      setState((prevToasts) => {
        const isTop = position.includes("top")

        /**
         * - If the toast is positioned at the top edges, the
         * recent toast stacks on top of the other toasts.
         *
         * - If the toast is positioned at the bottom edges, the recent
         * toast stacks below the other toasts.
         */
        const toasts = isTop
          ? [toast, ...(prevToasts[position] ?? [])]
          : [...(prevToasts[position] ?? []), toast]

        return {
          ...prevToasts,
          [position]: toasts,
        }
      })

      return id
    },

    update: (id, options) => {
      if (!id) return

      setState((prevState) => {
        const nextState = { ...prevState }
        const { position, index } = findToast(nextState, id)

        if (position && index !== -1) {
          nextState[position][index] = {
            ...nextState[position][index],
            ...options,
            message: createRenderToast(options),
          }
        }

        return nextState
      })
    },

    closeAll: ({ positions } = {}) => {
      // only one setState here for perf reasons
      // instead of spamming this.closeToast
      setState((prev) => {
        const allPositions: ToastPosition[] = [
          "bottom",
          "bottom-right",
          "bottom-left",
          "top",
          "top-left",
          "top-right",
        ]

        const positionsToClose = positions ?? allPositions

        return positionsToClose.reduce(
          (acc, position) => {
            acc[position] = prev[position].map((toast) => ({
              ...toast,
              requestClose: true,
            }))

            return acc
          },
          { ...prev } as ToastState,
        )
      })
    },

    close: (id) => {
      setState((prevState) => {
        const position = getToastPosition(prevState, id)

        if (!position) return prevState

        return {
          ...prevState,
          [position]: prevState[position].map((toast) => {
            // id may be string or number
            // eslint-disable-next-line eqeqeq
            if (toast.id == id) {
              return {
                ...toast,
                requestClose: true,
              }
            }

            return toast
          }),
        }
      })
    },

    isActive: (id) => Boolean(findToast(toastStore.getState(), id).position),
  }
}

/**
 * Static id counter to create unique ids
 * for each toast
 */
let counter = 0

/**
 * Create properties for a new toast
 */
function createToast(message: ToastMessage, options: CreateToastOptions = {}) {
  counter += 1
  const id = options.id ?? counter

  const position = options.position ?? "bottom"

  return {
    id,
    message,
    position,
    duration: options.duration,
    onCloseComplete: options.onCloseComplete,
    onRequestRemove: () => toastStore.removeToast(String(id), position),
    status: options.status,
    requestClose: false,
    containerStyle: options.containerStyle,
  }
}
