import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { clearAuthToken } from '../../redux/actions';

function Navigation() {
  const location = useLocation();
  const dispatch = useDispatch();
  const token = useSelector(state => state.auth.token);
  const isContactsRoute = location.pathname === '/contacts';

  const handleLogout = () => {
    dispatch(clearAuthToken());
    // Additional logout actions can be added here
  };

  // Hide navigation on the home route and for login/register links
  if (
    location.pathname === '/' ||
    location.pathname === '/login' ||
    location.pathname === '/register'
  ) {
    return null;
  }

  return (
    <nav>
      {token && isContactsRoute ? (
        <>
          <NavLink to="/">Home</NavLink>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : token ? (
        <>
          <NavLink to="/contacts">Contacts</NavLink>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : null}
    </nav>
  );
}

export default Navigation;
